import React, {useEffect, useState} from 'react';
import { useLocation } from "react-router-dom";
import { useNavigate } from "react-router-dom";
import Nav from './Anav'; 


function Allforms(){
    const navigate = useNavigate();
    const [forms, setForms] = useState([]);
    const {state} = useLocation();
    useEffect(() => {
        if (state === null){
            navigate('/sadmin')  
        }
      }, [state]);
    useEffect( () => {
        fetch('http://localhost:4000/all-forms') 
            .then(res => {
                return res.json();
            }).catch((error) => {
                alert('error with server ask for help')
                navigate('/adminhome')
            })
            .then(data => {
                setForms(data)
            })
    }, [forms]);

    const handleSubmit = (e, id, fnme, lnme, cnts, vnts) => {
        e.persist();
        e.preventDefault();
        const data= {
            formid: id,
            fname: fnme,
            lname: lnme,
            cnotes: cnts,
            vnotes: vnts
        }
        navigate('/viewform', {state: data})
    }
    const handleBack = (e) => {
        const data= {
            valid: true
        }
        navigate('/adminhome', {state: data})
    }

    return(
        <body>
            <Nav/>
            <section class="bg-light text-dark p-5">
            <div class="row g-3">
            <div class="col"><h1 class="text-warning" style={{paddingBottom: '30px'}}>All forms</h1></div>
            <div class="col"><form class="d-flex" role="search">
                    <input class="form-control me-2" type="search" placeholder="First name" aria-label="Search"/>
                    <input class="form-control me-2" type="search" placeholder="Last name" aria-label="Search"/>
                    <button class="btn btn-outline-success" type="submit">Search</button>
                </form></div>
            </div>


                <table class="table table-striped ">

                <tbody>
                {
            forms.map((form, index) => (
                <tr>
                    <td>{form.fname} {form.lname}</td>
                    <td>{form.fstatus}</td>
                    <td>Submission time: {new Date(form.date_form).toLocaleString()}</td>
                    <td><button type="button" class="btn" onClick={(e) => handleSubmit(e, form.formid, form.fname, form.lname, form.cnotes, form.vnotes )}>View Form</button></td>

                </tr>

            ))
            }
            </tbody>
         </table>
            </section>
        </body>
    );
}

export default Allforms;
