import Logo from './foodbank-logo.png';
import {Link} from 'react-router-dom';
import React, {useEffect, useState} from 'react';
import { useNavigate } from "react-router-dom";

function Nav(){
    const [status, setStatus] = useState([]);
    const [language, setLanguage] =useState()
    const navigate = useNavigate();
    useEffect( () => {
        fetch('http://localhost:4000/fbstatus') 
            .then(res => {
                return res.json();
            }).catch((error) => {
                alert('error with server ask for help')
            })
            .then(data => {
                setStatus(data)
            })
    }, []);

    const handleAdd = (e) => {
        navigate('/sadmin')
    }
    const handleVolun = (e) => {
        navigate('/svolun')
    }
    const handleForm = (e) => {
        if(status[0].fbstatus === 'Out of Stock'){
            navigate('/outofstock')
        }else{
            navigate('/sclient')
        }

    }

    const handleLanguage =(e, lan) => {
        e.persist();
        e.preventDefault();
        if(lan === 'English'){
            setLanguage(false)


        }else{
            setLanguage(true)

        }
    }
    return(

        <nav class="navbar navbar-expand-lg navbar-dark bg-dark">
            <div class="container">
            <Link to="/" className="navbar-brand"><img src={Logo} style={{ width: '20%' , height: '20%' }} alt="Coding Beauty logo"></img></Link>

            <button class= "navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navmenu">
                <span class="navbar-toggler-icon"></span>

            </button>
            <div class="collapse navbar-collapse" id="navmenu">
                    <ul class="navbar-nav ms-auto">
                        <li class= "nav-item ">
                         <button onClick={handleAdd} class="btn" style={{color: 'white'}}>{language ? 'Connexion des Admin' : 'Admin Sign-in'}</button>
                        </li>
                        <li class= "nav-item ">
                         <button onClick={handleVolun}  class="btn" style={{color: 'white'}}>{language ? 'Connexion des volontaires' : 'Volunteer Sign-in'}</button>
                        </li>
                        <li class= "nav-item">
                         <button onClick={handleForm}  class="btn" style={{color: 'white'}}>{language ? 'Formulaire' : 'Form'}</button>
                        </li>
                        <li class= "nav-item">
                        <div class="btn-group" role="group">
                                        <button type="button" class="btn btn-secondary dropdown-toggle" data-bs-toggle="dropdown" aria-expanded="false">
                                        {language ? 'Langue' : 'Language'}
                                        </button>
                                        <ul class="dropdown-menu text-center">
                                        <button className="btn" onClick={(e) => handleLanguage(e,'French')}>French</button>
                                        <button className="btn" onClick={(e) => handleLanguage(e,'English')}>English</button>
                                        </ul>
                                    </div>
                        </li>
                    </ul>
                </div>

            </div>

        </nav>
    );
}

export default Nav;
