import { useLocation } from "react-router-dom";
import axios from 'axios';
import { useNavigate } from "react-router-dom";
import Nav from './Anav'; 

function Climit(){
    const navigate = useNavigate();
    const [category, setCategory] = useState([]);
    const {state} = useLocation();
    const handleInput = (e) => {
        e.persist();
        setSearch({...search, [e.target.name]: e.target.value});
    }
    useEffect(() => {
        if (state === null){
            navigate('/sadmin')  
        }
      }, [state]);

    useEffect( () => {
        fetch('http://localhost:4000/all-category-limit') 
            .then(res => {
                return res.json();
            }).catch((error) => {

                navigate('/adminhome')
            })
            .then(data => {
                setCategory(data)
            })
    }, [forms]);

    const handleSubmit = (e, id, fnme, lnme) => {
        e.persist();
        e.preventDefault();
        const data= {
            formid: id,
            fname: fnme,
            lname: lnme
        }
        navigate('/lviewform', {state: data})
    }

    return(
        <body>
            <Nav/>   
            <section class="bg-light text-dark p-5">
            <div class="col"><h1 class="text-warning" style={{paddingBottom: '30px'}}>Category Limit</h1></div>
                <table class="table table-striped ">

                <tbody>
                {
            category.map((ctg, index) => (
                <tr>
                    <td>Category: {ctg.category}</td>
                    <td>Individual limit: {ctg.maximum}</td>
                    <td>Family limit: {ctg.maximum_f}</td>
                    <td><button type="button" class="btn" >Edit limits</button></td>

                </tr>

            ))
            }
            </tbody>
         </table>
            </section>
        </body>
    );
}

export default Climit;
