import React, {useEffect, useState} from 'react';
import { useLocation } from "react-router-dom";
import axios from 'axios';
import { useNavigate } from "react-router-dom";
import Nav from './Nav'; 

function Activeforms(){
    const navigate = useNavigate();
    const [forms, setForms] = useState([]);
    const {state} = useLocation();
    useEffect(() => {
        if (state === null){
            navigate('/sadmin')

        }
      }, [state]);

    useEffect( () => {
        fetch('http://localhost:4000/active-forms') 
            .then(res => {
                return res.json();
            }).catch((error) => {
                alert('error with server ask for help')
                navigate('/adminhome')
            })
            .then(data => {
                setForms(data)
            })
    }, []);

    const handleSubmit = (e, id, fnme, lnme, cnts, ants) => {
        e.persist();
        e.preventDefault();
        const data= {
            formid: id,
            fname: fnme,
            lname: lnme,
            cnotes: cnts,
            anotes: ants
        }
        navigate('/acviewform', {state: data})
    }
    const handleComplete = (e, id) => {
        e.persist();
        e.preventDefault();
        const data= {
            formid: id
        }
            axios.post('http://localhost:4000/update-completeform', data )
            .then( res => {

               alert('Order Completed')
               window.location.reload();

            })
            .catch((error) => {
                alert('Error please ask staff for help');
            });
    }

    const handleBack = (e) => {
        const data= {
            valid: true
        }
        navigate('/adminhome', {state: data})
    }
    return(
        <body>
                        <Nav/>
            <section class="bg-light text-dark p-5 ">
                <h1 class="text-warning" style={{paddingBottom: '30px'}}>Active forms</h1>
                <div class="col"><button class="btn btn-danger  w-100" type="button"  onClick={handleBack} style={{marginBottom: '30px'}} >Back to admin home page</button></div>
                <table class="table table-striped ">

                <tbody>
                {
            forms.map((form, index) => (
                <tr>
                    <td>{form.fname} {form.lname}</td>
                    <td>Status: {form.fstatus}</td>
                    <td>Volunteer: {form.vname}</td>
                    <td>Submission time: {new Date(form.date_form).toLocaleString()}</td>
                    <td><button type="button" class="btn" onClick={(e) => handleSubmit(e, form.formid, form.fname, form.lname, form.cnotes, form.anotes )}>View Form</button></td>
                    <td><button type="button" class="btn btn-danger" onClick={(e) => handleComplete(e, form.formid )}>Complete</button></td>

                </tr>

            ))
            }
            </tbody>
         </table>
            </section>
        </body>
    );
}

export default Activeforms;
