import react from 'react';
import React, {useEffect, useState} from 'react';
import { useLocation } from "react-router-dom";
import axios from 'axios';
import { useNavigate } from "react-router-dom";
import Nav from './Nav'; 
import Prompt from './Prompt';

function Cviewform(){

    const {state} = useLocation();
    const { formid, fname, lname, cnotes, vnotes } = state;
    const [items, setItems] = useState([]);
    const navigate = useNavigate();

    useEffect(() => {
        axios.get('http://localhost:4000/formview-items', {
            params: {
              formid: formid
            } 
        })
        .then(res => {
            setItems(res.data);
        })
        .catch((error) => {
            alert('error');
        });
      }, []);

      const handleSubmit = (e) => {

        navigate('/completedforms')
    }


    const handleLog = (e) => {
        e.persist();
        e.preventDefault();
        const data= {
            formid: formid
        }
            axios.post('http://localhost:4000/update-loggedform', data )
            .then( res => {

               alert('Order Logged')
               navigate('/completedforms')
            })
            .catch((error) => {
                alert('Error please ask staff for help');
            });
    }

    return(
        <body >
            <Nav />
            <section class="bg-light text-dark p-5 ">
                <h1 style={{paddingBottom: '20px'}}>Order</h1>
                <h4>{fname} {lname}</h4>
            <table class="table table-striped ">
                <tbody>
                     {
                    items.map((item, index) => (
                        <tr>
                            <td>{item.itname}</td>
                            <td>Amount: {item.amount}</td>
                        </tr>


                    ))
                    }
                </tbody>

            </table>
            <p>Customer notes: {cnotes}</p>
            <p>Volunteer notes: {vnotes}</p>
            <div class="btn-toolbar" role="toolbar" aria-label="Toolbar with button groups">
                 <div class="btn-group me-2" role="group" aria-label="First group"><button class="btn btn-secondary w-20" type="button"  onClick={handleLog} style={{marginTop: '40px'}} >Log</button></div>
                 <div class="btn-group me-2" role="group" aria-label="First group"><button class="btn btn-danger w-20" type="button"  onClick={handleSubmit} style={{marginTop: '40px'}} >Back</button></div>

            </div>

            </section>

        </body>
    );
}

export default Cviewform;
