import { useLocation } from "react-router-dom";
import React, {useEffect, useState} from 'react';
import { useNavigate } from "react-router-dom";
import axios from 'axios';
import Nav from './Anav'; 

function Searchresult(){
    const navigate = useNavigate();
    const {state} = useLocation();
    const [forms, setForms] = useState([]);
    useEffect(() => {
        if (state === null){
            navigate('/sadmin')
        }
      }, [state]);

      useEffect(() => {
        axios.get('http://localhost:4000/form-search', {
            params: {
              fname: state.fname,
              lname: state.lname
            } 
        })
        .then(res => {
            setForms(res.data);
        })
        .catch((error) => {
            alert('error with server');
        });
      }, [forms]);

      const handleSubmit = (e, id, fnme, lnme, cnts, vnts) => {
        e.persist();
        e.preventDefault();
        const data= {
            formid: id,
            fname: fnme,
            lname: lnme,
            cnotes: cnts,
            vnotes: vnts
        }
        navigate('/viewform', {state: data})
    }
    return(
        <body>
                        <Nav/>
            <section class="bg-light text-dark p-5 ">
                <h1 class="text-warning" style={{paddingBottom: '30px'}}>Submitted forms</h1>
                <table class="table table-striped ">

                <tbody>
                {
            forms.map((form, index) => (
                <tr>
                    <td>{form.fname} {form.lname}</td>
                    <td>Status: {form.fstatus}</td>
                    <td>Submission time: {new Date(form.date_form).toLocaleString()}</td>
                    <td><button type="button" class="btn" onClick={(e) => handleSubmit(e, form.formid, form.fname, form.lname, form.cnotes )}>View Form</button></td>

                </tr>

            ))
            }
            </tbody>
         </table>
            </section>
        </body>
    );
}

export default Searchresult;
