import react from 'react';
import React, {useEffect, useState} from 'react';
import { useLocation } from "react-router-dom";
import axios from 'axios';
import { useNavigate } from "react-router-dom";
import Nav from './Nav'; 
import Prompt from './Prompt';


function Formfruits(){
    const [isFormIncomplete, setIsFormIncomplete] = useState(true);
    const {state} = useLocation();
    const navigate = useNavigate();

    const {  fnme, lnme, family}=''
    const [change, setChange] = useState([]);
    const [etat, setEtat] = useState([]);
    const [items, setItems] = useState([]);
    const [data, setData] = useState([]);
    const [message, setMessage] = useState('');
    useEffect(() => {
        try{
            const {  fnme, lnme, family} = state;
        }catch(error){
            alert('error, please go through right steps to get to this page')
            navigate('/sclient')
        }
      }, []);

    useEffect(() => {
        const unloadCallback = (event) => {
          event.preventDefault();
          event.returnValue = "";
          return "";
        };

        window.addEventListener("beforeunload", unloadCallback);
        return () => window.removeEventListener("beforeunload", unloadCallback);
      }, []);

    useEffect( () => {
        axios.get('http://localhost:4000/items-category',{
            params: {
              category: 'Fruit'
            } 
        })
            .then(res => {
                setItems(res.data);
            })
            .catch((error) => {
                alert('errror');
            });
          }, []);

    useEffect(() => {
        axios.get('http://localhost:4000/formid', {
            params: {
              fnme: fnme,
              lnme: lnme
            } 

        })
        .then(res => {
            console.log(fnme)
            setData(state);
        })
        .catch((error) => {
            alert('errror');
        });
      }, []);

      useEffect(() => {
        if (!isFormIncomplete) {
            const data= {
                formid: first.formid,
                cnotes: message,
                fnme: fnme,
                lnme: lnme,
                family: family,
            }
        navigate('/formvegetables', {state: data})
        }
      }, [isFormIncomplete]);

      var first = data[0];

    const handleSubmit = async (e) => {

        e.persist();
        e.preventDefault();

        axios.get('http://localhost:4000/total-category', {
            params: {
              formid: first.formid,
              category: 'Fruit'
            } 
        }) 
        .then( res => {
            if(family){
                if(res.data[0].sum > 20){
                    alert('Reduce total quantity by updating form')
                }else{
                    setIsFormIncomplete(false);
                }

            }else{
                if(res.data[0].sum > 15){
                    alert('Reduce total quantity by updating form')
                }else{
                    setIsFormIncomplete(false)
                }
            }   
        })
        .catch((error) => {
            alert('Error please ask staff for help');
        });






    }
    const handleItem =(e, itname, itmax , index) => {
        e.persist();
        e.preventDefault();

        if(parseInt(etat[index].quantity) === 0){
            const data1 ={
                formid: first.formid,
                itname: itname
            }

            axios.post('http://localhost:4000/update-deleteformitem-0', data1 )
                        .then( res => {

                            const newChange = [...change];
                            newChange[index] = {
                           ...newChange[index],
                             ['chg']: false
                              };
                              setChange(newChange);

                        })
                        .catch((error) => {
                            alert('Error please ask staff for help');
                        });
        }else{
            if(family){
                if (etat[index].quantity > (itmax * 2) || etat[index].quantity < 0){
                    alert('Quantity invalid')
               } else{
                    if(change[index].chg === false){
                        const data= {
                            itname:  itname,
                            formid: first.formid,
                            amount: etat[index].quantity,
                        }

                        axios.post('http://localhost:4000/add-formitem', data )
                        .then( res => {

                            const newChange = [...change];
                            newChange[index] = {
                           ...newChange[index],
                             ['chg']: true
                              };
                              setChange(newChange);

                        })
                        .catch((error) => {
                            alert('Error please ask staff for help');
                        });
                    }else{
                        const data= {
                            itname:  itname,
                            formid: first.formid,
                            amount: etat[index].quantity,
                        }

                        axios.post('http://localhost:4000/update-items', data )
                        .then( res => {

                        })
                        .catch((error) => {
                            alert('Error please ask staff for help');
                        });
                    }
               }
            }else{
                if (etat[index].quantity > itmax || etat[index].quantity < 0){
                    alert('Quantity invalid')
               } else{
                    if(change[index].chg === false){
                        const data= {
                            itname:  itname,
                            formid: first.formid,
                            amount: etat[index].quantity,
                        }

                        axios.post('http://localhost:4000/add-formitem', data )
                        .then( res => {

                            const newChange = [...change];
                            newChange[index] = {
                           ...newChange[index],
                             ['chg']: true
                              };
                              setChange(newChange);

                        })
                        .catch((error) => {
                            alert('Error please ask staff for help');
                        });
                    }else{
                        const data= {
                            itname:  itname,
                            formid: first.formid,
                            amount: etat[index].quantity,
                        }

                        axios.post('http://localhost:4000/update-items', data )
                        .then( res => {

                        })
                        .catch((error) => {
                            alert('Error please ask staff for help');
                        });
                    }
               }
            }
        }


    }

    const handleChange = (e, i) => {

        if(etat[i] === undefined){
            const newChange = [...change];
            newChange[i] = {
           ...newChange[i],
             ['chg']: false
              };
              setChange(newChange);
        }
        e.persist();
      const newEtat = [...etat];
      newEtat[i] = {
        ...newEtat[i],
        [e.target.name]: e.target.value
      };
      setEtat(newEtat);
    }



    const handleMessageChange = event => {

      setMessage(event.target.value);
    }

    return(
        <section>
            <Nav />
            <body class="bg-light text-dark p-5">
            <h1 >List of availbale Fruits</h1>

            <section><h2 class=' text-center bg-success'>Maximum of 15 Fruits, and 20 for families</h2></section>
            <div class="h-100 card-group ">

            {
            items.map((item, index) => (

                <div class="p-3">

                <div class="card" style={{width: '18rem'}}>
                    <div class="card-body">
                    <form class="needs-validation"  onSubmit={(e) => handleItem(e,item.itname, item.max, index)} novalidate>
                        <h5 class="card-title">{item.itname}</h5>
                        <p class="card-text">Category: {item.category}</p>
                        <h6 class="card-text">Selection: {item.description}</h6>
                        <input class="form-control" type="number" placeholder="Quantity"  name="quantity" onChange={(e) => handleChange(e, index )} aria-label="default input example" required />
                        <p class="card-text text-danger">Limit: {family ? (  item.max * 2) : (item.max)}</p>
                        <button type="submit" class="btn btn-secondary" >{((change[index] === undefined) ||(change[index].chg === false) ) ? 'Add to form' : 'Update form'}</button>
                        </form>
                    </div>
                </div>
            </div>
            ))
            }

            </div>
            <textarea class="form-control "  rows="3"        id="notes"  onChange={handleMessageChange} style={{height: '5rem'}} type="text" placeholder="Addtional comments"  name="cnotes" aria-label="default input example"/>

            <button type="button" className="btn btn-secondary m-3" onClick={handleSubmit} >Next</button>

            </body>
            <Prompt
       when={isFormIncomplete}
       message="Please do not leave form until submitted, Click on CANCEL" />
        </section>
    );
}

export default Formfruits;
