import React, {useEffect, useState} from 'react';
import { useLocation } from "react-router-dom";
import axios from 'axios';
import { useNavigate } from "react-router-dom";
import Nav from './Anav'; 

function Submittedforms(){
    const navigate = useNavigate();
    const [forms, setForms] = useState([]);
    const {state} = useLocation();
    const [search, setSearch] = useState({
        fname: '',
        lname: ''
    })
    const handleInput = (e) => {
        e.persist();
        setSearch({...search, [e.target.name]: e.target.value});
    }
    useEffect(() => {
        if (state === null){
            navigate('/sadmin')
        }
      }, [state]);
    useEffect( () => {
        fetch('http://localhost:4000/submitted-forms') 
            .then(res => {
                return res.json();
            }).catch((error) => {
                navigate('/adminhome')
            })
            .then(data => {
                setForms(data)
            })
    }, [forms]);

    const handleSubmit = (e, id, fnme, lnme, cnts) => {
        e.persist();
        e.preventDefault();
        const data= {
            formid: id,
            fname: fnme,
            lname: lnme,
            cnotes: cnts
        }
        navigate('/sviewform', {state: data})
    }

    const handleSearch = (e) => {
        const data= {
            fname: search.fname,
            lname: search.lname,
            status: `and fstatus='submitted'`
        }
        navigate('/searchresult', {state: data})
    }
    const handleDelete = (e, id) => {
        e.persist();
        e.preventDefault();
        const data= {
            formid: id
        }
            axios.post('http://localhost:4000/update-deleteform', data )
            .then( res => {

            })
            .catch((error) => {
                alert('Error please ask staff for help');
            });
    }
    return(
        <body>
                        <Nav/>
            <section class="bg-light text-dark p-5 ">
            <div class="row g-3">
            <div class="col"><h1 class="text-warning" style={{paddingBottom: '30px'}}>Submitted forms</h1></div>
            <div class="col"><form class="d-flex" onSubmit={handleSearch} role="search">
                    <input class="form-control me-2" type="search" name="fname" id="validationCustom01" onChange={handleInput} placeholder="First name" aria-label="Search" required/>
                    <input class="form-control me-2" type="search" name="lname" id="validationCustom01" onChange={handleInput} placeholder="Last name" aria-label="Search" required/>
                    <button class="btn btn-outline-success" type="submit">Search</button>
                </form></div>
            </div>
                <table class="table table-striped ">

                <tbody>
                {
            forms.map((form, index) => (
                <tr>
                    <td>{form.fname} {form.lname}</td>
                    <td>Status: {form.fstatus}</td>
                    <td>Submission time: {new Date(form.date_form).toLocaleString()}</td>
                    <td><button type="button" class="btn" onClick={(e) => handleSubmit(e, form.formid, form.fname, form.lname, form.cnotes )}>View Form</button></td>
                    <td><button type="button" class="btn btn-danger" onClick={(e) => handleDelete(e, form.formid)}>Delete</button></td>

                </tr>

            ))
            }
            </tbody>
         </table>
            </section>
        </body>
    );
}

export default Submittedforms;
