import React, {useEffect, useState} from 'react';
import { useLocation } from "react-router-dom";
import axios from 'axios';
import { useNavigate } from "react-router-dom";
import Nav from './Nav'; 


function Allforms(){
    const navigate = useNavigate();
    const [forms, setForms] = useState([]);
    const {state} = useLocation();
    useEffect(() => {
        if (state === null){
            navigate('/sadmin')
            alert('please follow right steps to get here')

        }
      }, [state]);
    useEffect( () => {
        fetch('http://localhost:4000/all-forms') 
            .then(res => {
                return res.json();
            }).catch((error) => {
                alert('error with server ask for help')
                navigate('/adminhome')
            })
            .then(data => {
                setForms(data)
            })
    }, []);

    const handleSubmit = (e, id, fnme, lnme, cnts, vnts) => {
        e.persist();
        e.preventDefault();
        const data= {
            formid: id,
            fname: fnme,
            lname: lnme,
            cnotes: cnts,
            vnotes: vnts
        }
        navigate('/viewform', {state: data})
    }
    const handleBack = (e) => {

        navigate('/adminhome')
    }

    return(
        <body>
            <Nav/>
            <section class="bg-light text-dark p-5">
                <h1 style={{paddingBottom: '30px'}}>All forms</h1>
                <div class="col"><button class="btn btn-secondary  w-100" type="button"  onClick={handleBack} style={{marginBottom: '30px'}} >Back to admin home page</button></div>
                <table class="table table-striped ">

                <tbody>
                {
            forms.map((form, index) => (
                <tr>
                    <td>{form.fname} {form.lname}</td>
                    <td>{form.fstatus}</td>
                    <td>{new Date(form.date_form).toDateString()}</td>
                    <td><button type="button" class="btn" onClick={(e) => handleSubmit(e, form.formid, form.fname, form.lname, form.cnotes, form.vnotes )}>View Form</button></td>

                </tr>

            ))
            }
            </tbody>
         </table>
            </section>
        </body>
    );
}

export default Allforms;
